        static void Main(string[] args)
        {
            using var context = new MyDbContext();
            context.Articulo_Secciones.RemoveRange(context.Articulo_Secciones);
            context.Articulo_Hashtags.RemoveRange(context.Articulo_Hashtags);
            context.Articulos.RemoveRange(context.Articulos);
            context.Hashtags.RemoveRange(context.Hashtags);

            GenerateHashes(context);
            GeneratePosts(context);

            context.SaveChanges();
            GenerateHashtags(context);
            context.SaveChanges();

            Console.WriteLine("Funciono");
        }

        static void GenerateHashtags(MyDbContext context)
        {
            var random = new Random();

            var minHashId = context.Hashtags.Min(x => x.Id);
            var maxHashId = context.Hashtags.Max(x => x.Id);

            var minPostId = context.Articulos.Min(x => x.Id);
            var maxPostId = context.Articulos.Max(x => x.Id);

            for (int i = minPostId; i < maxPostId; i++)
            {
                context.Articulo_Hashtags.Add(new ArticuloHashtag { ArticuloId = i, HashtagId = random.Next(minHashId, maxHashId) });
                context.Articulo_Hashtags.Add(new ArticuloHashtag { ArticuloId = i, HashtagId = random.Next(minHashId, maxHashId) });
                context.Articulo_Hashtags.Add(new ArticuloHashtag { ArticuloId = i, HashtagId = random.Next(minHashId, maxHashId) });
            }
        }

        static void GeneratePosts(MyDbContext context)
        {
            var fakerSeccion = new Faker<Seccion>()
                .RuleFor(x => x.Contenido, f => f.Lorem.Paragraphs(20))
                .RuleFor(x => x.SubTitulo, f => f.Lorem.Words(4).Aggregate((a, b) => $"{a} {b}"))
                .RuleFor(x => x.Tipo, f => "Texto");

            var fakerArticulo = new Faker<Articulo>()
                .RuleFor(x => x.Titulo, f => f.Lorem.Words(20).Aggregate((a, b) => $"{a} {b}"))
                .RuleFor(x => x.Autor, f => $"{f.Name.FirstName()} {f.Name.LastName()}")
                .RuleFor(x => x.Secciones, f => fakerSeccion.Generate(5).ToList());
            //.RuleFor(x => x.ArticuloHashtags, f => new List<ArticuloHashtag>
            //{
            //    new ArticuloHashtag { HashtagId = random.Next(minId, maxId) },
            //    new ArticuloHashtag { HashtagId = random.Next(minId, maxId) },
            //    new ArticuloHashtag { HashtagId = random.Next(minId, maxId) },
            //});

            var articulos = fakerArticulo.Generate(1000);

            context.Articulos.AddRange(articulos);
        }

        static void GenerateHashes(MyDbContext context)
        {
            var hashtags = Hashes.Select(x => new Hashtag { Name = x });

            context.Hashtags.AddRange(hashtags);
        }
